  package telusko;

public class Array1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int nums[] = { 4, 5, 2, 8 }; // index-0,1,2,3
		System.out.println(nums[3]);
		String students[] = { "Sandeep", "Lipsa", "Sangram", "Sunil" };
		System.out.println(students[2]);
		int num1[] = new int[4];
		num1[0] = 5;
		num1[1] = 8;
		num1[2] = 5;
		num1[3] = 6;
		for (int i = 0;i<=3; i++) {
			System.out.println(num1[i]);

		}

	}

}
