package telusko;

public class Switch2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String season="Raja";
		switch (season) {
		case "Winter":
			System.out.println("I am wearing a Hoodie");
			break;
		case "Summer":
			System.out.println("I have nothing to wear");
			break;
		case "Raja":
			System.out.println("My life my rule");
			break;
		case "Autumn":
			System.out.println("AC is cool");
			break;
		default:
			System.out.println("Mind your own business");
			break;
		}
		

	}

}
